    }
    console.log(isLoadong)

    setIsLoadong(false)
  },[])

  console.log(isLoadong)
  console.log(toursApi)
  return (
    <>

      <main>
        {<Loading/> && isLoadong}
        {/* <Tours api={toursApi}/> */}
      </main>

    </>

  )

}

export default App
