import React, { useState } from 'react';

const Tour = ({id,image,name,price,info,remove}) => {

  const [toueArry,settoueArry] = useState();
  const [check,setcheck] = useState(true);
  const [readMore, setReadMore] = useState(false)

  const readBtn = <button onClick={()=>{setReadMore(!readMore)}}>
    {readMore ? 'Show less' : 'Show more' }
  </button>;





  return (

      <article  className='single-tour'>
        <img src={image} alt={name} />
        <footer>
          <div className='tour-info'>
            <h4>{name}</h4>
            <h4 className='tour-price'>${price}</h4>
          </div>
          <p>{readMore ? info : `${info.substring(0,200)}`} {readBtn}</p>
        <button onClick={()=>remove} className='delete-btn'>Not interested</button>
        </footer>

      </article>

  )

};

export default Tour;
