  const [toursApi, setToursApi] = useState([]);
  const [isLoadong, setIsLoadong] = useState(true);

  useEffect(()=>{
    setIsLoadong(true)

      const fechData = async () => {
      const response = await fetch(url)
      const api = await response.json()
