      }

      fechData();




    console.log(isLoadong)

    setIsLoadong(false)
  },[])

  console.log(isLoadong)
  console.log(toursApi)
  return (
    <>

      <main>
        {isLoadong ? <Loading/> : <Tours api={toursApi}/> }

      </main>

    </>

  )

}

export default App
