function App() {
  const [toursApi, setToursApi] = useState([]);
  const [isLoadong, setIsLoadong] = useState(false);

  useEffect(()=>{
    setIsLoadong(true)
    try{
          const fechData = async () => {
          const response = await fetch(url)
          const api = await response.json()
          setToursApi(api)
          fechData();
          setIsLoadong(false)
    } 

    }catch (err){

    }


  },[])
  console.log(toursApi)
  return (
    <>

      <main>
        {<Loading/> && isLoadong}
        <Tours api={toursApi}/>
      </main>

    </>

  )

}

export default App
